    <!-- 正则表达式首页输入框 -->
    <div class="form-group">
      <div class="col-sm-12">
        <textarea class="form-control" onmouseover="this.focus();" id="textSour" name="textSour" rows="10"
          placeholder="请输入要验证的文本"></textarea>
      </div>
    </div>
    <!-- 正则表达式等输入框 -->
    <div class="form-group">
      <div class="col-sm-12">
        <div class="input-group">
          <span style="text-align: center;">
             正则表达式 
          </span>
          <br>
          <input type="text" class="form-control" id="textPattern" name="textPattern" placeholder="请输入自定义正则表达式"
