    <!-- 验证、清空、复制按钮 -->
    <div class="col-sm-8">
      <input type="button" onclick="return onMatch();" value="验证匹配内容" class="btn btn-success button-width-10">
      <input type="button" onclick="Empty();" value="清空输入框" class="btn btn-danger button-width-10">
