<script>
  // 简化的DOM元素查询函数，返回匹配的第一个元素
  function $(selector) {
    return document.querySelector(selector);
  }

  // 简化的DOM元素查询函数，返回匹配的所有元素的 NodeList
  function $$(selector) {
    return document.querySelectorAll(selector);
  }
</script>

<script>
  // 复制指定元素内容到剪贴板
  var copyText = function() {
    var text = document.getElementById('input'); // 获取ID为'input'的元素
    // 选中文本
    text.select()
    // 复制
    document.execCommand('copy')
  }
  var copyText2 = function() {
    var text = document.getElementById('textReplaceResult'); // 获取ID为'textReplaceResult'的元素
    // 选中文本
    text.select()
    // 复制
    document.execCommand('copy')
  }
  var copyText3 = function() {
    var text = document.getElementById('textMatchResult'); // 获取ID为'textMatchResult'的元素
    // 选中文本
    text.select()
    // 复制
    document.execCommand('copy')
  }
</script>

<script>
  // 向文本替换区域追加指定的字符，并聚焦
  function pp(a){
    $("#textReplace").value += a.replace(/\\/,''); // 追加字符，并将反斜杠替换为空
    $("#textReplace").focus() // 聚焦到文本替换区域
  }
  // 禁用ID为'bt1'的按钮
  function dis(){
    document.getElementById("bt1").disabled = true;
  }
  // 启用ID为'bt1'的按钮
  function di(){
    document.getElementById("bt1").disabled = false;
  }
  // 获取输入的HTML链接并格式化
  function getInput1(){
    var text = $('#input').value; // 获取ID为'input'的元素的值
    var list = text.match(/<a.*?href="([^"]+)".*?>([\s\S]+?)<\/a>/g); // 使用正则表达式匹配所有链接
    var html = "";
    if(list.length == 0){
      html = "格式化失败！！！请检查是否为带html标签"; // 如果没有匹配的链接，显示错误信息
    } else {
      for(var i in list){
        var a = list[i].match(/<a.*?href="([^"]+)".*?>([\s\S]+?)<\/a>/); // 匹配单个链接
        html += a[2] + "::" + a[1] + "\n"; // 将链接文本和链接地址用'::'连接并换行
      }
    }
    $('#input').value = html; // 将格式化后的文本设置为'input'元素的值
  }

  // 在JSON和文本格式之间转换
  function getInput2(){
    var text = $('#input').value; // 获取ID为'input'的元素的值
    if(text.match(/\&\&|::/g)){
      text = text.replace(/\&\&/g,'\n');
      var list = text.match(/.*::.*/g);
      var html = [];
      for(var i = 0; i < list.length; i++){
        var l = list[i].match(/(.*)::(.*)/);
        var title = l[1]; var url = l[2]
        html.push({title: title, url: url, style: {}}); // 将标题和链接地址作为对象推入数组
      }
      $('#input').value = JSON.stringify(html); // 将数组转换为JSON字符串并设置为'input'元素的值
    } else {
      var html = "";
      var json = JSON.parse(text); // 将JSON字符串转换为对象
      for(var i in json){
        html += json[i].title + "::" + json[i].url + "\n";
      }
      $('#input').value = html; // 将文本设置为'input'元素的值
    }
  }
  // 根据标志调整JSON对象的样式属性
  function getInput3(flag){
    var text = $('#input').value; // 获取ID为'input'的元素的值
    var json = JSON.parse(text); // 将JSON字符串转换为对象
    if(flag == 1){
      for(var i in json){
        json[i].style.layout_flexBasisPercent = 0.2; // 设置布局的flex-basis百分比
      }
    } else if(flag == 2){
      for(var i in json){
        json[i].style.layout_flexBasisPercent = 0.25;
      }
    } else if(flag == 3){
      for(var i in json){
        json[i].style.layout_flexBasisPercent = 0.4;
      }
    } else if(flag == 4){
      for(var i in json){
        json[i].style.layout_flexBasisPercent = 1;
      }
    } else if(flag == 5){
      for(var i in json){
        json[i].style.layoutflexGrow = 1; // 设置布局的flex-grow属性
      }
    }
    $('#input').value = JSON.stringify(json); // 将对象转换为JSON字符串并设置为'input'元素的值
  }
  // 设置元素的可见性
  function setVisible(idElement, visible) {
    var obj = document.getElementById(idElement);
    obj.style.visibility = visible ? "visible" : "hidden";
  }
  // 验证输入字段是否有效
  function isValidFields() {
    var textSour = document.getElementById("textSour");
    if (null == textSour.value || textSour.value.length < 1) {
      textSour.focus();
      JsonsMessageBox($(textSour), "请输入待匹配文本");
      return false;
    }
    var textPattern = document.getElementById("textPattern");
    if (null == textPattern.value || textPattern.value.length < 1) {
      textPattern.focus();
      JsonsMessageBox($(textPattern), "请输入正则表达式");
      return false;
    }
    return true;
  }
  // 构建正则表达式对象
  function buildRegex() {
    var op = "";
    if (document.getElementById("optionGlobal").checked) op = "g";
    if (document.getElementById("optionIgnoreCase").checked) op += "i";
    return new RegExp(document.getElementById("textPattern").value, op);
  }
  // 执行匹配操作
  function onMatch() {
    if (!isValidFields())
      return false;
    document.getElementById("textMatchResult").value = "";
    var regex = buildRegex();
    var result = document.getElementById("textSour").value.match(regex);
    if (null == result || 0 == result.length) {
      document.getElementById("textMatchResult").value = "（没有匹配）";
      return false;
    }
    if (document.getElementById("optionGlobal").checked) {
      var re = "";
      var strResult = "共找到 " + result.length + " 处匹配：\r\n";
      document.getElementById("textResult").innerText = strResult;
      for (var i = 0; i < result.length; ++i) { re += result[i] + "\r\n"; }
      document.getElementById("textMatchResult").value = re;
    } else {
      document.getElementById("textMatchResult").value = "匹配位置：" + regex.lastIndex + "\r\n匹配结果：" + result[0];
    }
    return true;
  }
  // 执行替换操作
  function onReplace() {
    var str = document.getElementById("textSour").value;
    var regex = buildRegex();
    document.getElementById("textReplaceResult").value = str.replace(regex, document.getElementById("textReplace").value);
  }
  // 清除输入发现字段
  function clearInput() {
  	document.getElementById("input").value = "";
 	}
  // 重置所有输入字段
  function reset() {
    $("#textSour").value = "";
    $("#textPattern").value = "";
    $("#textMatchResult").value = "";
    $("#textReplace").value = "";
    $("#textReplaceResult").value = "";
  }
  // 点击事件处理，设置正则表达式并执行匹配
  $("#regCommon a").click(function () {
    $("#textPattern").val($(this).attr("t"));
    onMatch();
  });
  // 清空所有输入字段
  function Empty() {
    $("#textSour").value = "";
    $("#textMatchResult").value = "";
    $("#textReplace").value = "";
    $("#textReplaceResult").value = "";
    $("#textSour").select();
  }
</script>

</html>

